import React from "react";
import './../css/sifaris.css';
import {Header} from "./header";
export class Sifaris extends React.Component{
    constructor() {
        super();
        this.state = {
            sifaris_selected : "ao",
        }
    }
    render() {
        return (
            <div id='sifaris'>
                <Header/>
                <div id="container">
                    <div id="title">
                        योजना सम्बन्धि सिफारिस
                    </div>
                    <div id="select_sifaris">
                        <span>योजना खोज्नुहोस</span>
                        <input type="text" placeholder="योजनाको नाम "/>
                        <div id='controls'>
                            <button style={this.state.sifaris_selected === "ao" ? {color: 'white',backgroundColor: 'green'} : {}} onClick={()=>{this.setState({sifaris_selected:"ao"})}}>खाता संचालन सिफारिस</button>
                            <button style={this.state.sifaris_selected === "ac" ? {color: 'white',backgroundColor: 'crimson'} : {}} onClick={()=>{this.setState({sifaris_selected:"ac"})}}>खाता बन्द सिफारिस</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
