import React, {Fragment} from "react";
import './../css/option_entry.css';
import {Header} from "./header";
import axios from "axios";
let bs = require('bikram-sambat');
export class OptionEntry extends React.Component{
    constructor() {
        super();
        this.state = {
            biwaran:{
                selectedBiwaran:'',
                bank:{
                    name:'hello',
                    addr:'www',
                    branch:'dddd',
                },
                ppa:{
                    name:'santosh',
                    phone:'45545454',
                },
                lagatBehorneSrot:{
                    srot:'hello srot'
                },
                padadhikariPada:{
                    pada:'',
                    level:'',
                },
                ward:{
                    name:'',
                    number:'',
                },
                totalWardWiseProject:{
                    ward_id:'',
                    total:'',
                }
            },
            errors:[],
            status:'',
            isRecordUpdated:true,
            ward_options:[],
            aaBaList:[],

        }
        this.handleValueChange=this.handleValueChange.bind(this);
        this.handleSubmit =  this.handleSubmit.bind(this);
    }
    componentDidMount() {
        axios({
            method: 'post',
            url: localStorage.getItem('server')+'api/getOptions',
            data: {options:'ward',fy:this.state.aa_ba},
        }).then((response)=> {
            console.log(response.data.ward_options);
            this.setState({ward_options:response.data.ward_options});
        }).catch((res) => {

        });
        var currentAaba = parseInt(localStorage.getItem('currentAaBa'));
        var aaBaList = this.state.aaBaList;
        for(let i=0;i<=5;i++){
            aaBaList.push(currentAaba+i+'/'+(currentAaba+1+i).toString().substr(2,2));
        }
        this.setState({aaBaList:aaBaList});

    }
    handleValueChange(event){
        this.setState({errors:[]});
        this.setState({status:''});
        let name = event.target.name;
        let value = event.target.value;
        let biwaran = this.state.biwaran;
        if(name==="selectedBiwaran"){biwaran.selectedBiwaran=value}
        if(name==="bankName"){biwaran.bank.name=value;}
        if(name==="bankAddr"){biwaran.bank.addr=value}
        if(name==="bankBranch"){biwaran.bank.branch=value;}
        if(name==="ppaName"){biwaran.ppaName=value;}
        if(name==="ppaNumber"){biwaran.ppaNumber=value;}
        if(name==="lagatBehorneSrotName"){biwaran.lagatBehorneSrotName=value;}
        if(name==="padadhikariPada"){biwaran.padadhikariPada=value;}
        if(name==="padadhikarLevel"){biwaran.padadhikarLevel=value;}
        if(name==="wardName"){biwaran.wardName=value;}
        if(name==="wardNumber"){biwaran.wardNumber=value;}
        if(name==="totalWardWiseProjectTotal"){biwaran.totalWardWiseProjectTotal=value;}
        if(name==="totalWardWiseProjectWardId"){biwaran.totalWardWiseProjectWardId=value;}
        this.setState({biwaran:biwaran});
    }
    handleSubmit(){
        this.setState({status:'adding ... '});
        axios({
            method: 'post',
            url: localStorage.getItem('server')+'api/put_option',
            data:this.state.biwaran,
        }).then((response)=> {
            console.log(response.data);
            // this.setState({errors:[]});
            // this.setState({status:''});
            // this.setState({isRecordUpdated:false},()=>{
            //     this.setState({isRecordUpdated:true});
            // });
            // if(response.data[0]===0){
            //     let errors = this.state.errors;
            //     response.data.forEach(obj => {
            //         Object.entries(obj).forEach(([key, value]) => {
            //             for(let i=0;i<=value.length;i++){
            //                 errors.push(value[i]);
            //             }
            //         });
            //     });
            //     this.setState({errors:errors});
            // }else if (response.data[0]===1){this.setState({status:response.data[1]});
            // }else{this.setState({status:'Something went wrong.'});}
        }).catch((res) => {this.setState({status:'Something went wrong.'});});
    }
    render() {
        return (
            <div id='option_entry'>
                <Header/>
                <div id="container">
                    <div id="title">
                        विवरणहरु अध्यावधिक गर्नुहोस
                    </div>
                    <div id="entry">
                        <div id="select_item">
                            <select name="selectedBiwaran" onChange={this.handleValueChange}>
                                <option selected={true} disabled={true}>--विवरणहरु--</option>
                                <option value="banks">बैङ्क सम्बन्धि विवरण</option>
                                <option value="ppa">प्रमुख प्रशासकीय अधिकृत सम्बन्धि विवरण</option>
                                <option value="lagatBehorneSrot">लागत व्यहोर्ने स्रोतहरु</option>
                                <option value="padadhikariPada">पदाधिकारी पद</option>
                                <option value="ward">वडा विवरण</option>
                                <option value="totalWardWiseProject">योजना विवरण</option>
                            </select>
                        </div>
                        {this.state.biwaran.selectedBiwaran==="banks" ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>बैङ्कको नाम</span>
                                    <input name="bankName" value={this.state.biwaran.bank.name} onChange={this.handleValueChange} type="text" placeholder="नाम"/>
                                </div>
                            </div>
                            <div className='item'>
                                <div id="input">
                                    <span>शाखा कार्यालय</span>
                                    <input name="bankBranch" value={this.state.biwaran.bank.branch} onChange={this.handleValueChange} type="text" placeholder="शाखा"/>
                                </div>
                            </div>
                            <div className='item'>
                                <div id="input">
                                    <span>बैङ्कको ठेगाना</span>
                                    <input name="bankAddr" value={this.state.biwaran.bank.addr} onChange={this.handleValueChange} type="text" placeholder="ठेगाना"/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.biwaran.selectedBiwaran==="ppa" ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>प्रमुख प्रशासकीय अधिकृतको नाम, थर</span>
                                    <input name="ppaName" value={this.state.data.ppaName} onChange={this.handleValueChange} type="text" placeholder="प्रमुख प्रशासकीय अधिकृतको नाम, थर"/>
                                </div>
                            </div>
                            <div className='item'>
                                <div id="input">
                                    <span>प्रमुख प्रशासकीय अधिकृतको सम्पर्क नं.</span>
                                    <input name="ppaPhone" value={this.state.data.ppaPhone} onChange={this.handleValueChange} type="text" placeholder="प्रमुख प्रशासकीय अधिकृतको सम्पर्क नं."/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.data.selected_option===3 ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>लागत व्यहोर्ने स्रोत</span>
                                    <input name="lagat_behorne_srot" value={this.state.data.lagat_behorne_srot} onChange={this.handleValueChange} type="text" placeholder="लागत व्यहोर्ने स्रोत"/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.data.selected_option===4 ?
                            <Fragment>
                                <div className="item">
                                    <div id="input">
                                        <span>पदाधिकारी पद</span>
                                        <input name="padadhikari_pada" value={this.state.data.padadhikari_pada} onChange={this.handleValueChange} type="text" placeholder="पदाधिकारी पद"/>
                                    </div>
                                </div>
                                <div className="item">
                                    <div id="input">
                                        <span>पदाधिकारी स्तर</span>
                                        <input name="padadhikari_level" value={this.state.data.padadhikari_level} onChange={this.handleValueChange} type="text" placeholder="पदाधिकारी स्तर"/>
                                    </div>
                                </div>
                            </Fragment> : null }
                        {this.state.data.selected_option===5 ?
                            <Fragment>
                                <div className="item">
                                    <div id="input">
                                        <span>वडा नम्बर</span>
                                        <input name="ward_number" value={this.state.data.ward_number} onChange={this.handleValueChange} type="text" placeholder="वडा नं."/>
                                    </div>
                                </div>
                                <div className='item'>
                                    <div id="input">
                                        <span>वडाको नाम</span>
                                        <input name="ward_name" value={this.state.data.ward_name} onChange={this.handleValueChange} type="text" placeholder="वडाको नाम"/>
                                    </div>
                                </div>
                            </Fragment> : null }
                        {this.state.data.selected_option===6 ?
                            <Fragment>
                                <div className="item">
                                    <span>आर्थिक बर्ष</span>
                                    <select name="aa_ba" value={this.state.aa_ba} onChange={this.handleValueChange}>
                                        <option selected={true} disabled={true} >-- आ.व. चयन गर्नुहोस --</option>
                                        {this.state.aaBaList.map((item,index)=>{
                                            return <option key={index} value={item}>{item}</option>
                                        })}
                                    </select>
                                </div>
                                <div className="item">
                                    <span>वार्ड नं. </span>
                                    <select name="yojanaharuWardNumber" onChange={this.handleValueChange}>
                                        <option selected={true} disabled={true} >-- वडा चयन गर्नुहोस --</option>
                                        {this.state.ward_options.map((item,index)=>{
                                            return <option key={index} value={item.id}>वार्ड नं. {item.ward_number}, {item.ward_name}</option>
                                        })}
                                    </select>
                                </div>
                                <div className='item'>
                                    <div id="input">
                                        <span>योजना संख्या</span>
                                        <input name="yojanaharuJamma" value={this.state.data.yojanaharuJamma} onChange={this.handleValueChange} type="text" placeholder="संख्या"/>
                                    </div>
                                </div>
                            </Fragment> : null }
                        <div id="control">
                            <div id="error">
                                {this.state.errors.map((error,index)=>{
                                    return <span key={index}>{error}</span>
                                })}
                            </div>
                            <div id="status">
                                <span>{this.state.status}</span>
                            </div>
                            <button onClick={this.handleSubmit}>थप्नुहोस</button>
                        </div>
                    </div>
                    {this.state.isRecordUpdated ? <Record  biwaran={this.state.biwaran}/> : null }
                </div>
            </div>
        );
    }
}
class Record extends React.Component{
    constructor() {
        super();
        this.state={
            receivedDetail:{
                banks:[],
            },
            padadhikari_pada_options:[],
            lagat_behorne_srot_options:[],
            ppa_options:[],
            bank_options:[],
            ward_options:[],
            totalWardWiseYojanaharu:[],
        }
    }
    componentWillMount() {
        axios({
            method: 'post',
            url: localStorage.getItem('server')+'api/getOptions',
            data: {detail:'all'},
        }).then((response)=> {
            let receivedDetail=this.state.receivedDetail;
            receivedDetail = response.data;
            this.setState({receivedDetail: receivedDetail});
            // if (response.data.lagat_behorne_srot_options) {
            //     this.setState({lagat_behorne_srot_options: response.data.lagat_behorne_srot_options})
            // }
            // if (response.data.padadhikari_pada_options) {
            //     this.setState({padadhikari_pada_options: response.data.padadhikari_pada_options})
            // }
            // if (response.data.ppa_options) {
            //     this.setState({ppa_options: response.data.ppa_options});
            // }
            // if (response.data.bank_options) {
            //     this.setState({bank_options: response.data.bank_options});
            // }
            // if (response.data.ward_options) {
            //     this.setState({ward_options: response.data.ward_options});
            // }
            // if (response.data.totalWardWiseYojanaharu) {
            //     this.setState({totalWardWiseYojanaharu: response.data.totalWardWiseYojanaharu});
            // }
        }).catch((res)=>{

        })
    }
    render(){
        return(
            <div id="record">
                {this.props.biwaran.selectedBiwaran==="banks" ?
                    <Fragment>
                        <div id="title">

                        </div>
                        <table>
                            <tr>
                                <td>क्र.सं.</td>
                                <td>नाम</td>
                                <td>शाखा</td>
                                <td>ठेगाना</td>
                                <td>सम्पादन</td>
                            </tr>
                            {this.state.receivedDetail.banks.map((bank, index)=>{
                                return  <tr key={index}>
                                    <td>{index+1}</td>
                                    <td>{bank.name}</td>
                                    <td>{bank.branch}</td>
                                    <td>{bank.addr}</td>
                                    <td><span>सम्पादन</span></td>
                                </tr>
                            })}
                        </table>
