import React, {Fragment} from "react";
import './../css/report.css';
import './../css/reportPrintPreview.css'
import {Header} from "./header";
import axios from "axios";
import {Link} from "react-router-dom";
import ReactToPrint from "react-to-print";
export class Report extends React.Component{
    constructor() {
        super();
        this.state = {
            projects:[],
            searchingValue:{
                upabhokta_samitiko_naam:'',
                aayojana_bajet_mathi:'',
                aayojana_bajet_tala:'',
                name:'',
                aayojana_hune_woda:0,
                aa_ba:null,
            },
            showOption:'search',
            searchedProjects:[],
            wardOptions:[],
        }
        this.handleSearch=this.handleSearch.bind(this);
        this.handleValueChange=this.handleValueChange.bind(this);
    }
    handleValueChange(event){
        var value = event.target.value;
        var name = event.target.name;
        let searchingValue= this.state.searchingValue;
        searchingValue.name='';
        searchingValue.upabhokta_samitiko_naam='';
        searchingValue.aayojana_bajet_mathi='';
        searchingValue.aayojana_bajet_tala='';
        this.setState({searchingValue:searchingValue},()=>{
            if(name==="upabhokta_samitiko_naam"){
                searchingValue.upabhokta_samitiko_naam = value;
            }
            if(name==="aayojana_bajet_mathi"){
                searchingValue.aayojana_bajet_mathi = value;
            }
            if(name==="aayojana_bajet_tala"){
                searchingValue.aayojana_bajet_tala = value;
            }
            if(name==="name"){
                searchingValue.name = value;
            }
            if (name === "aayojana_hune_woda") {
                searchingValue.aayojana_hune_woda = value;
            }
            if (name ==="aa_ba") {
                searchingValue.aa_ba = value;
                this.setState({searchingValue: searchingValue}, () => {
                    axios({
                        method: 'post',
                        url: localStorage.getItem('server')+'api/getProjects',
                        data: {aa_ba:this.state.searchingValue.aa_ba.substr(0,2)},
                    }).then((response)=> {
                        this.setState({projects:response.data,searchedProjects:response.data});
                    }).catch((res)=>{

                    });
                })
            }
            this.setState({searchingValue:searchingValue},()=>{
                this.setState({searchedProjects: []},()=>{
                    var searchedProjects = this.state.searchedProjects;
                    var isEmpty = (this.state.searchingValue.name === '' && this.state.searchingValue.upabhokta_samitiko_naam === '' && this.state.searchingValue.aayojana_bajet_mathi === "" && this.state.searchingValue.aayojana_bajet_tala === '');
                    this.state.projects.forEach((item,index,arr)=>{
                        if(this.state.searchingValue.upabhokta_samitiko_naam !=='' && (parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.upabhokta_samitiko_naam.includes(this.state.searchingValue.upabhokta_samitiko_naam)){
                            searchedProjects.push(item);
                        }else if(this.state.searchingValue.name !== '' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.name.includes(this.state.searchingValue.name)){
                            searchedProjects.push(item);
                        } else if(this.state.searchingValue.aayojana_bajet_mathi!=='' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.lagat_anuman>=this.state.searchingValue.aayojana_bajet_mathi){
                            searchedProjects.push(item);
                        } else if(this.state.searchingValue.aayojana_bajet_tala!=='' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.lagat_anuman<=this.state.searchingValue.aayojana_bajet_tala){
                            searchedProjects.push(item);
                        } else if( parseInt(this.state.searchingValue.aayojana_hune_woda)===0){
                            searchedProjects.push(item);
                        }else if(parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)){
                            searchedProjects.push(item);
                        }
                    });
                    this.setState({searchedProjects: searchedProjects});
                });
            });
        });
    }
    handleSearch(){
        this.setState({searchedProjects: []},()=>{
            let searchedProjects = this.state.searchedProjects;
            this.state.projects.forEach((item,index,arr)=>{
                if(this.state.searchingValue.upabhokta_samitiko_naam !=='' && (parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.upabhokta_samitiko_naam.includes(this.state.searchingValue.upabhokta_samitiko_naam)){
                    searchedProjects.push(item);
                }else if(this.state.searchingValue.name !== '' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.name.includes(this.state.searchingValue.name)){
                    searchedProjects.push(item);
                } else if(this.state.searchingValue.aayojana_bajet_mathi!=='' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.lagat_anuman>=this.state.searchingValue.aayojana_bajet_mathi){
                    searchedProjects.push(item);
                } else if(this.state.searchingValue.aayojana_bajet_tala!=='' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.lagat_anuman<=this.state.searchingValue.aayojana_bajet_tala){
                    searchedProjects.push(item);
                } else if(this.state.searchingValue.aayojana_bajet_tala!=='' && ( parseInt(this.state.searchingValue.aayojana_hune_woda)===0 || parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)) && item.lagat_anuman<=this.state.searchingValue.aayojana_bajet_tala){
                    searchedProjects.push(item);
                }else if(parseInt(this.state.searchingValue.aayojana_hune_woda)===0){
                    searchedProjects.push(item);
                }else if(parseInt(item.aayojana_hune_woda)===parseInt(this.state.searchingValue.aayojana_hune_woda)){
                    searchedProjects.push(item);
                }
            });
            this.setState({searchedProjects: searchedProjects});
        });
    }
    componentDidMount() {
        let searchingValue = this.state.searchingValue;
        searchingValue.aa_ba = localStorage.getItem('aa_ba');
        this.setState({searchingValue:searchingValue});
        axios({
            method: 'post',
            url: localStorage.getItem('server')+'api/getProjects',
            data: {aa_ba:this.state.searchingValue.aa_ba.substr(0,2)},
        }).then((response)=> {
            this.setState({projects:response.data,searchedProjects:response.data});
        }).catch((res)=>{

        });
        axios({
            method: 'post',
            url: localStorage.getItem('server')+'api/getOptions',
            data: {options:'ward'},
        }).then((response)=> {
            let wardOptions = response.data.ward_options;
            this.setState({wardOptions:wardOptions});
        }).catch((res)=>{

        })
    }
    render() {
        return (
            <div id='report'>
                <Header/>
                <div id="container">
                    <div id="title">
                         
                        {this.context.aarthik_barsa}
                    </div>
                    {this.state.showOption==="search" ?
                    <div id="project">
                        <div id="search_options">
                            <div className="item">
                                <span>  </span>
                                <input name="upabhokta_samitiko_naam" value={this.state.searchingValue.upabhokta_samitiko_naam} onChange={this.handleValueChange} type="text" placeholder="  "/>
                            </div>
                            <div className="item">
                                <span>  </span>
                                <input name='name' value={this.state.searchingValue.name} onChange={this.handleValueChange} type="text" placeholder="  "/>
                            </div>
                            <div className="item">
                                <span>   </span>
                                <input name='aayojana_bajet_mathi' value={this.state.searchingValue.aayojana_bajet_mathi} onChange={this.handleValueChange} type="text" placeholder="   "/>
                            </div>
                            <div className="item">
                                <span>   </span>
                                <input name='aayojana_bajet_tala' value={this.state.searchingValue.aayojana_bajet_tala} onChange={this.handleValueChange}  type="text" placeholder="   "/>
