                                    {this.state.wardOptions.map((wardOption,index)=>{
                                        return <option key={index} value={wardOption.ward_number}>{wardOption.ward_number} . , {wardOption.ward_name}</option>
                                    })}
                                </select>
                            </div>
                            <div className="item">
                                <select name="aa_ba" onChange={this.handleValueChange}>
                                    <option value={localStorage.getItem('aa_ba')}>{localStorage.getItem('aa_ba')}</option>
