                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.biwaran.selectedBiwaran==="lagatBehorneSrot" ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>  </span>
                                    <input name="lagatBehorneSrotName" value={this.state.biwaran.lagatBehorneSrotName} onChange={this.handleValueChange} type="text" placeholder="  "/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.biwaran.selectedBiwaran==="padadhikariPada" ?
                            <Fragment>
                                <div className="item">
                                    <div id="input">
                                        <span></span>
                                        <input name="padadhikariPadaName" value={this.state.biwaran.padadhikariPada.name} onChange={this.handleValueChange} type="text" placeholder=" "/>
                                    </div>
                                </div>
