                                    </div>
                                </div>
                            </Fragment> : null }
                        {this.state.biwaran.selectedBiwaran==="ward" ?
                            <Fragment>
                                <div className="item">
                                    <div id="input">
                                        <span> </span>
                                        <input name="wardNumber" value={this.state.biwaran.ward.number} onChange={this.handleValueChange} type="text" placeholder=" ."/>
                                    </div>
                                </div>
                                <div className='item'>
                                    <div id="input">
                                        <span> </span>
                                        <input name="wardName" value={this.state.biwaran.ward.name} onChange={this.handleValueChange} type="text" placeholder=" "/>
                                    </div>
                                </div>
                            </Fragment> : null }
                        {this.state.biwaran.selectedBiwaran==="totalWardProject"?
                            <Fragment>
                                <div className="item">
                                    <span> . </span>
                                    <select name="totalWardProjectWardId" onChange={this.handleValueChange}>
                                        <option selected={true} disabled={true} >--  --</option>
                                        {this.state.dataReceived.wards.map((ward,index)=>{
                                            return <option key={index} value={ward.id}> . {ward.number}, {ward.name}</option>
                                        })}
                                    </select>
                                </div>
                                <div className='item'>
