    useLayoutEffect(() => {
        const elem = document.querySelector(".active")
        if (elem) {
            elem.scrollIntoView()
        }
    })
    return (
        <div className="drop-down">
            <div className="option-wrapper">
                <ul>
                    {options.map((option, index) => (
                        <li
                            key={index}
                            className={option.value === value ? "active" : ""}
                            onClick={() => {
                                onSelect(option)
                            }}
                        >
                            {option.label}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    )
}

