import { ADToBS } from "bikram-sambat-js"
import React, { Fragment, FunctionComponent, useCallback, useEffect, useState } from "react"
import { parseBSDate, stitchDate } from "../../Helpers/helpers"
import { useConfig } from "../Config"
import { ParsedDate, parsedDateInitialValue, SplittedDate } from "../types/types"
import CalenderController from "./components/CalenderController"
import DayPicker from "./components/pickers/DayPicker"

interface CalenderProps {
    value: string
    onChange: (value: string, clicked: boolean) => void
}

const Calender: FunctionComponent<CalenderProps> = ({ value, onChange }) => {
    const { setConfig } = useConfig()
    const [isInitialized, setIsInitialized] = useState<boolean>(false)
    const [selectedDate, setSelectedDate] = useState<ParsedDate>(parsedDateInitialValue)
    const [calenderDate, setCalenderDate] = useState<ParsedDate>(parsedDateInitialValue)
    const [daySelected, setDaySelected] = useState<boolean>(false)

    useEffect(() => {
        if (isInitialized) {
            onChange(
                stitchDate({ year: selectedDate.bsYear, month: selectedDate.bsMonth, day: selectedDate.bsDay }),
                daySelected,
            )
        }
    }, [selectedDate, isInitialized])

    useEffect(() => {
        setConfig("currentLocale", "ne")
        const parsedDateValue = parseBSDate(value)

        setSelectedDate(parsedDateValue)
        setCalenderDate(parsedDateValue)
        setIsInitialized(true)
    }, [value])

    const onPreviousMonthHandler = useCallback(() => {
        setCalenderDate(date => {
            let year = date.bsYear
            let month = date.bsMonth - 1

            if (month < 1) {
                month = 12
                year--
            }

            return parseBSDate(
                stitchDate(
                    {
                        day: date.bsDay,
                        month,
                        year,
                    },
                    "-",
                ),
            )
        })
    }, [])

    const onNextMonthClickHandler = useCallback(() => {
        setCalenderDate(date => {
            let year = date.bsYear
            let month = date.bsMonth + 1

            if (month > 12) {
                month = 1
                year++
            }

            return parseBSDate(
                stitchDate(
                    {
                        day: date.bsDay,
                        month,
                        year,
                    },
                    "-",
                ),
            )
        })
    }, [])

    const onTodayClickHandler = useCallback(() => {
        const today = parseBSDate(ADToBS(new Date()))

        setCalenderDate(today)
        setSelectedDate(today)
    }, [])

    const onYearSelectHandler = useCallback(
        year => {
            setCalenderDate(parseBSDate(stitchDate({ year, month: calenderDate.bsMonth, day: calenderDate.bsDay })))
        },
        [calenderDate],
    )

    const onMonthSelectHandler = useCallback(
        month => {
            setCalenderDate(parseBSDate(stitchDate({ year: calenderDate.bsYear, month, day: calenderDate.bsDay })))
        },
        [calenderDate],
    )

    const onDaySelectHandler = useCallback((date: SplittedDate) => {
        const newDate = parseBSDate(stitchDate(date))

        setCalenderDate(newDate)
        setSelectedDate(newDate)
        setDaySelected(true)
    }, [])

    return (
        <div className="nepali-date-picker">
            <div className="calendar-wrapper">
                {isInitialized && (
                    <Fragment>
                        <CalenderController
                            onPreviousMonth={onPreviousMonthHandler}
                            onNextMonth={onNextMonthClickHandler}
                            calenderDate={calenderDate}
                            onToday={onTodayClickHandler}
                            onYearSelect={onYearSelectHandler}
                            onMonthSelect={onMonthSelectHandler}
                        />

                        <DayPicker
                            selectedDate={selectedDate}
                            calenderDate={calenderDate}
                            onDaySelect={onDaySelectHandler}
                        />
                    </Fragment>
                )}
            </div>
        </div>
    )
}

export default Calender
function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;
import { ADToBS } from "bikram-sambat-js"
import React, { FunctionComponent, useEffect, useRef, useState } from "react"
import { Calender } from "./Calender"
import { ConfigProvider } from "./Config"
import "./NepaliDatePicker.scss"
interface DatePickerOptions {
    className?: string
    defaultDate?: string | boolean | undefined
    placeholder?: string
    disabled?: boolean
    showResetDateButton?: boolean
    resetButtonElement?: any
    onDateSelect: (date: string | boolean | undefined) => any
}

const NepaliDatePicker: FunctionComponent<DatePickerOptions> = ({
    className,
    defaultDate,
    placeholder,
    onDateSelect,
    disabled,
    showResetDateButton,
    resetButtonElement,
}) => {
    const [showCalendar, setShowCalendar] = useState(false)
    const dateEl = useRef<HTMLInputElement>(null)
    const today = ADToBS(new Date())
    const [value, setValue] = useState(defaultDate === true ? today : defaultDate)

    const handleOnChange = (date: string | undefined, daySelected: boolean) => {
        setValue(date)
        daySelected ? setShowCalendar(false) : setShowCalendar(true)
    }

    useEffect(() => {
        onDateSelect(value)
    }, [value])

    const clearDate = (defaultDate: string | boolean | undefined) => {
        defaultDate === true ? setValue(today) : setValue(defaultDate)
        setShowCalendar(false)
    }

    return (
        <ConfigProvider>
            <div className="input-group" id="dateInputGroup">
                <input
                    type="text"
                    className={className}
                    readOnly={true}
                    ref={dateEl}
                    value={value || ""}
                    disabled={disabled}
                    onClick={() => setShowCalendar(!showCalendar)}
                    placeholder={placeholder}
                />
                {showResetDateButton && (
                    <div className="input-group-append">
                        <button
                            className="btn btn-outline-secondary"
                            type="button"
                            id="resetDateButton"
                            onClick={() => clearDate(defaultDate)}
                        >
                            {resetButtonElement}
                        </button>
                    </div>
                )}
            </div>
            {showCalendar && <Calender value={value || ADToBS(new Date())} onChange={handleOnChange} />}
        </ConfigProvider>
    )
}

NepaliDatePicker.defaultProps = {
    className: "nepali-datepicker",
    disabled: false,
    defaultDate: undefined,
    placeholder: "कृपया मिति चयन गर्नुहोस्।",
    showResetDateButton: false,
    resetButtonElement: "X",
    onDateSelect: () => {},
}

export default NepaliDatePicker
