import React, {Fragment} from "react";
import './../css/report.css';
import './../css/reportPrintPreview.css'
import {Header} from "./header";
import axios from "axios";
import {Link} from "react-router-dom";
import ReactToPrint from "react-to-print";
export class Report extends React.Component{
    constructor() {
        super();
        this.state = {
            projects:[],
            searchingValue:{
                upabhokta_samitiko_naam:'a',
                aayojana_bajet_mathi:'',
                aayojana_bajet_tala:'',
                name:'b',
                wardId:0,
                aa_ba:'aa_ba',
            },
            showOption:'search',
            searchedProjects:[],
            wards:[],
        }
        this.handleValueChange=this.handleValueChange.bind(this);
    }
    handleValueChange(event){
        var value = event.target.value;
        var name = event.target.name;
        let searchingValue= this.state.searchingValue;
        if(name==="upabhokta_samitiko_naam"){
            searchingValue.upabhokta_samitiko_naam = value;
        }
        if(name==="name"){
            searchingValue.name = value;
        }
        this.setState({searchingValue: searchingValue},()=>{
            let searchingKey = [];
            if(this.state.searchingValue.upabhokta_samitiko_naam !=='') {
                searchingKey.push(this.state.searchingValue.upabhokta_samitiko_naam);
            }
            if(this.state.searchingValue.name !=='') {
                searchingKey.push(this.state.searchingValue.name);
            }
            let searchingQuery='&&';
            searchingKey.forEach((item,index,arr)=>{
                searchingQuery+item;
            })
            console.log(searchingQuery);
        });
        // searchingValue.name='';
