        //     }
        //     if(name==="name"){
        //         searchingValue.name = value;
        //     }
        //     if (name === "wardId") {
        //         searchingValue.wardId = value;
        //     }
        //     if (name ==="aa_ba") {
        //         searchingValue.aa_ba = value;
        //         this.setState({searchingValue: searchingValue}, () => {
        //             console.log(this.state.searchingValue.aa_ba);
        //             axios({
        //                 method: 'post',
        //                 url: localStorage.getItem('server')+'api/getProjects',
        //                 data: {aa_ba:this.state.searchingValue.aa_ba},
        //             }).then((response)=> {
        //                 let searchingValue= this.state.searchingValue;
        //                 searchingValue.name='';
        //                 searchingValue.upabhokta_samitiko_naam='';
        //                 searchingValue.aayojana_bajet_mathi='';
        //                 searchingValue.aayojana_bajet_tala='';
        //                 searchingValue.wardId=0;
        //                 this.setState({searchingValue:searchingValue,projects:response.data,searchedProjects:response.data});
        //             }).catch((res)=>{
        //
        //             });
        //         })
        //     }
        //     this.setState({searchingValue:searchingValue},()=>{
