        //             var searchedProjects = this.state.searchedProjects;
        //             var isEmpty = (this.state.searchingValue.name === '' && this.state.searchingValue.upabhokta_samitiko_naam === '' && this.state.searchingValue.aayojana_bajet_mathi === "" && this.state.searchingValue.aayojana_bajet_tala === '');
