import React from "react";
import './../css/sifaris.css';
import {Header} from "./header";
import ReactToPrint from 'react-to-print';
import { NepaliDatePicker } from "datepicker-nepali-reactjs";
import axios from "axios";
export class Sifaris extends React.Component{
    constructor() {
        super();
        this.state = {
            sifaris_selected : "ao",
            aayojana_naam:'',
            searchedProjects:[],
            projectId:null,
            project:[],
            padadhikariharu:[],
        }
        this.handleUpdate = this.handleUpdate.bind(this);
        this.handleGetSifaris = this.handleGetSifaris.bind(this);
    }
    handleGetSifaris(id,aayojana_naam,aayojana_sthal){
        this.setState({aayojana_naam:aayojana_naam +' '+ aayojana_sthal,searchedProjects:[],projectId:id},()=>{
            axios({
                method: 'post',
                url: localStorage.getItem('server')+'api/getSifaris',
                data: {id:this.state.projectId},
            }).then((response)=>{
                this.setState({project:response.data[1],padadhikariharu:response.data[2]});
                console.log(response.data);
            }).catch(function (error) {
                console.log(error);
            });
        })
    }
    handleUpdate(event){
        if(event.target.name==="aayojana_naam"){
            this.setState({aayojana_naam:event.target.value},()=>{
                axios({
                    method: 'post',
                    url: localStorage.getItem('server')+'api/searchProject',
                    data: {aayojana_naam:event.target.value},
                }).then((response)=>{
                    this.setState({searchedProjects:response.data[1]})
                   console.log(response.data);
                }).catch(function (error) {
                    console.log(error);
                });
            });
        }
    }
    render() {
        return (
            <div id='sifaris'>
                <Header/>
                <div id="container">
                    <div id="title">
                        योजना सम्बन्धि विभिन्न सिफारिसहरु
                    </div>
                    <div id="project">
                        <div id="entry">
                            <div id="select_sifaris">
                                <button style={this.state.sifaris_selected === "ao" ? {color: 'white',backgroundColor: 'green'} : {}} onClick={()=>{this.setState({sifaris_selected:"ao"})}}>खाता संचालन सिफारिस</button>
                                <button style={this.state.sifaris_selected === "ac" ? {color: 'white',backgroundColor: 'green'} : {}} onClick={()=>{this.setState({sifaris_selected:"ac"})}}>खाता बन्द सिफारिस</button>
                            </div>
                            <div id="select_people">
                                <input type="checkbox" checked="checked"/> <span>अध्यक्ष</span>
                                <input type="checkbox" checked="checked"/> <span>सचिव</span>
                                <input type="checkbox" checked="checked"/> <span>कोषादध्क्ष</span>
                            </div>
                            <div id="sifaris_data">
                                <div className="item">
                                    <div id="input">
                                        <span>योजना खोज्नुहोस</span>
                                        <input name="aayojana_naam" value={this.state.aayojana_naam} onChange={this.handleUpdate} type="text" placeholder="योजनाको नाम "/>
                                    </div>
                                    {this.state.searchedProjects.length>0 ?
                                    <div id="result">
                                        {this.state.searchedProjects.map((item,index)=>{
                                            return <span onClick={()=>{this.handleGetSifaris(item.id,item.aayojanako_naam,item.aayojanako_sthal)}} className="item">{item.aayojanako_naam}, {item.aayojanako_sthal}</span>
                                        })}
                                    </div> : null }
                                </div>
                                <div className="item">
                                    <div id="input">
                                        <span>बैङ्कको नाम र ठेगाना</span>
                                        <select>
                                            <option>श्री प्रभु बैङ्क, थासाङ शाखा, मुस्ताङ</option>
                                            <option>श्री प्रभु बैङ्क, जोमसोम शाखा, मुस्ताङ</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="item">
                                    <div id="input">
                                        <span>पत्रको मिति</span>
                                        <NepaliDatePicker onDateSelect={(date)=>{}} className="date_picker"  defaultDate={false} placeholder="पत्रको मिति" />
                                    </div>
                                </div>
                                {this.state.sifaris_selected==="ac" ?
                                    <div className="item">
                                        <div id="input">
                                            <span>खाता नं.</span>
                                            <input type="text" placeholder="खाता नं."/>
                                        </div>
                                    </div> : null}
                            </div>
                            <div id="controls">
                                <ReactToPrint
                                    trigger={() => {
                                        return <button>प्रिन्ट गर्नुहोस</button>
                                    }}
                                    content={() => this.componentRef}
                                />
                                <button>रद्ध गर्नुहोस</button>
                            </div>
                        </div>
                        <div id="print_preview">
                            <SifarisPrint padadhikariharu={this.state.padadhikariharu} project={this.state.project} ref={el => (this.componentRef = el)} />
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
class SifarisPrint extends React.PureComponent {
    constructor() {
        super();
        this.state={
            upabhokta_samitiko_naam : this.props.project.upabhokta_samitiko_naam,
            aayojanako_naam : this.props.project.aayojanako_naam,
            aayojanako_sthal : this.props.project.aayojanako_sthal,
            aayojana_suru_miti : this.props.project.aayojana_suru_miti,
            gathan_vayeko_miti :this.props.project.gathan_vayeko_miti,

            padadhikariharu_name:'',
            padadhikariharu_pada:1,
            padadhikariharu_thegana:'',
            padadhikariharu_na_na:'',

            aayojana_hune_woda:this.props.project.aayojana_hune_woda,
        }
    }
    componentDidMount() {

    }
    render() {
        return (
            <div id='sifaris_a4'>
                <div id="top">
                    <img src={require('../../files/emblem_logo.png').default}/>
                    <div id="title">
                        <span>थासाङ गाउँपालिका</span>
                        <span>गाउँकार्यपालिकाको कार्यालय</span>
                        <span>कोबाङ, मुस्ताङ</span>
                    </div>
                    <div id="foo">
                        <div id="left">
                            <span>पत्र संख्या : </span>
                            <span>चलानी नं. : </span>
                        </div>
                        <div id="right">
                            <span>गण्डकी प्रदेश, नेपाल</span>
                        </div>
                    </div>
                </div>
                <div id="date"> मिति :  </div>
                <div id="office">बैङ्कको नाम, <br/>शाखा कार्यालय,<br/> ठेगाना ।</div>
                <div id="subject">खाता संचालनको लागि सिफारिस गरिएको । </div>
                <div id="body">
                    प्रस्तुत विषयमा थासाङ गाउँपालिकाको आ.व. 077/78 को बार्षिक स्वीकृत कार्यक्रम अनुसार <b>{ this.state.aayojanako_naam}</b> लागि
                    मिति <b>{this.props.project.gathan_vayeko_miti}</b> को वडा नं. {this.state.aayojana_hune_woda} को आम भेलाले <b>{}</b> (<b>{}</b>) को
                    अध्यक्षामा <b>सदस्य संख्या</b> सदस्यीय <b>{this.state.upabhokta_samitiko_naam}</b> गठन
                    भई मिति <b>{this.state.aayojana_suru_miti}</b> मा यस कार्यालय सँग योजना सम्झौता भइसकेकोले श्री <b>{}</b>, श्री <b>{}</b> र श्री <b>कोषाध्क्षको नाम</b> बाट <b>बैङ्को नाम</b> मा संयुक्त खाता
                    संलागन गर्न सिफारिससाथ अनुरोध छ ।
                </div>
                <div id="sign">
                    ..................................
                </div>
            </div>
        );
    }
}
