                                <td>क्र.सं.</td><td>पदाधिकारी पदको नाम</td><td>पदाधिकारी पदको स्तर</td><td>सम्पादन</td>
                            </tr>
                            {this.state.receivedDetail.padadhikariPadas.map((padadhikariPada, index)=>{
                                return  <tr key={index}>
                                    <td>{index+1}</td>
                                    <td>{padadhikariPada.name}</td>
                                    <td>{padadhikariPada.level}</td>
                                    <td>डिलेट गर्नहोस</td>
                                </tr>
                            })}
                        </table>
                    </Fragment> : null }
                {this.props.biwaran.selectedBiwaran==="ward" ?
                    <Fragment>
                        <table>
                            <tr>
                                <td>क्र.सं.</td><td>वडा नाम</td><td>वडा नम्बर</td><td>सम्पादन</td>
                            </tr>
                            {this.state.receivedDetail.wards.map((ward, index)=>{
