import React, {Fragment} from "react";
import './../css/option_entry.css';
import {Header} from "./header";
import ReactToPrint from 'react-to-print';
import { NepaliDatePicker } from "datepicker-nepali-reactjs";
export class OptionEntry extends React.Component{
    constructor() {
        super();
        this.state = {
            selected_option:1,
        }
        this.updateText=this.updateText.bind(this);
    }
    updateText(event){
        let name = event.target.name;
        let value = event.target.value;
        if(name==="select"){
            this.setState({selected_option:parseInt(value)})
        }
    }
    render() {
        return (
            <div id='option_entry'>
                <Header/>
                <div id="container">
                    <div id="title">
                        विवरणहरु अध्यावधिक गर्नुहोस
                    </div>
                    <div id="entry">
                        <div id="select_item">
                            <select name="select" onChange={this.updateText}>
                                <option value="1">बैङ्क सम्बन्धि विवरण</option>
                                <option value="2">प्रमुख प्रशासकीय अधिकृत सम्बन्धि विवरण</option>
                                <option value="3">लागत व्यहोर्ने स्रोतहरु</option>
                            </select>
                        </div>
                        {this.state.selected_option===1 ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>बैङ्कको नाम</span>
                                    <input type="text" placeholder="बैङ्क नाम"/>
                                </div>
                            </div>
                            <div className='item'>
                                <div id="input">
                                    <span>बैङ्कको ठेगाना</span>
                                    <input type="text" placeholder="बैङ्कको ठेगाना"/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.selected_option===2 ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>प्रमुख प्रशासकीय अधिकृतको नाम, थर</span>
                                    <input type="text" placeholder="प्रमुख प्रशासकीय अधिकृतको नाम, थर"/>
                                </div>
                            </div>
                            <div className='item'>
                                <div id="input">
                                    <span>प्रमुख प्रशासकीय अधिकृतको सम्पर्क नं.</span>
                                    <input type="text" placeholder="प्रमुख प्रशासकीय अधिकृतको सम्पर्क नं."/>
                                </div>
                            </div>
                        </Fragment> : null }
                        {this.state.selected_option===3 ?
                        <Fragment>
                            <div className="item">
                                <div id="input">
                                    <span>लागत व्यहोर्ने स्रोत</span>
                                    <input type="text" placeholder="लागत व्यहोर्ने स्रोत"/>
                                </div>
                            </div>
                        </Fragment> : null }
                    </div>
                </div>
            </div>
        );
    }
}
